import React, { useState } from 'react';

import './App.css'

const App = () => {
  // const message = 'Hello World'
  const [message, setMessage] = useState('hello world!')  // utilizando o state. Só quando o state é atualizado que o componente em si é atualizado
  return (
    <>
    <div className="container">{message}</div>
    <button onClick={() => setMessage("heloooooooooo")}>
      mudar mensagem
    </button>
    </>
  )
}

export default App
